$(document).ready(function(){navigator.geolocation&&navigator.geolocation.watchPosition(function(e){var t=e.coords.latitude,n=e.coords.longitude;$.cookie("latitude",t,{expires:7,path:"/"}),$.cookie("longitude",n,{expires:7,path:"/"})},function(e){ga("send","event","error","geolocation",e.code)})});
